export class Encoder {
  bitDepth: any;
  constructor(bitDepth) {
    this.bitDepth = bitDepth;
  }
  encode(buffer, value, index) {
    const dv = new DataView(buffer.buffer);
    switch (this.bitDepth) {
      case 32:
        dv.setFloat32(index * 4, value, true);
        break;
      case 16:
        value = Math.min(Math.max(-1, value), 1);
        value < 0
          ? dv.setInt16(index * 2, value * 0x8000, true)
          : dv.setInt16(index * 2, value * 0x7fff, true);
        break;
      case 8:
        buffer.writeUInt8(value, index * Uint8Array.BYTES_PER_ELEMENT);
        break;
      default:
        throw new Error("unsupported bitdepth");
    }
  }
}
export class Decoder {
  bitDepth: any;
  constructor(bitDepth) {
    this.bitDepth = bitDepth;
  }
  decode(buffer, index) {
    const dv = new DataView(buffer.buffer);
    switch (this.bitDepth) {
      case 32:
        return dv.getFloat32(index * 4, true);
      case 16:
        return dv.getInt16(index * 2, true);
      case 8:
        return dv.getUint8(index * 2);
      default:
        throw new Error("unsupported bitdepth");
    }
  }
}

import { openSync, readFileSync, readSync } from "fs";
import { SSRContext } from "./ssrctx";

var writeString = function (view, offset, str) {
  return str.split("").map(function (char, idx) {
    view.setUint8(offset + idx, char.charCodeAt(0));
  });
};
/*
	`52 49 46 46 24 08 00 00 57 41 56 45 66 6d 74 20 10 00 00 00 01 00 02 00 
22 56 00 00 88 58 01 00 04 00 10 00 64 61 74 61 00 08 00 00 00 00 00 00 
24 17 1e f3 3c 13 3c 14 16 f9 18 f9 34 e7 23 a6 3c f2 24 f2 11 ce 1a 0d`;
*/

export const wavHeader = function (
  length,
  sampleRate = 44100,
  numChannels = 2,
  bitDepth = 16
) {
  var sampleRate = sampleRate;
  var numChannels = numChannels;
  var samples = length;
  var buffer = new ArrayBuffer(48);
  var view = new DataView(buffer);
  /* RIFF identifier */
  writeString(view, 0, "RIFF");
  /* RIFF chunk length */
  view.setUint32(4, 36 + samples.length * 2, true);
  /* RIFF type */
  writeString(view, 8, "WAVE");
  /* format chunk identifier */
  writeString(view, 12, "fmt ");
  /* format chunk length */
  view.setUint32(16, bitDepth, true);
  /* sample format (raw) */
  view.setUint16(20, 1, true);
  /* channel count */
  view.setUint16(22, numChannels, true);
  /* sample rate */
  view.setUint32(24, sampleRate, true);
  /* byte rate (sample rate * block align) */
  view.setUint32(28, (sampleRate * bitDepth) / 8, true);
  /* block align (channel count * bytes per sample) */
  view.setUint16(32, (numChannels * bitDepth) / 8, true);
  /* bits per sample */
  view.setUint16(34, bitDepth, true);
  /* data chunk identifier */
  writeString(view, 36, "data");
  // /* data chunk length */
  view.setUint32(40, (samples.length * bitDepth) / 8, true);
  return view.buffer;
};
export const readHeader = (path: string) => {
  const fd = openSync(path, "r");
  const ob = Buffer.alloc(48);
  readSync(fd, ob, 0, 48, 0);
  const view = new DataView(ob.buffer);
  const [nChannels, sampleRate, _, __, bitDepth] = [
    view.getUint16(22, true),
    view.getUint32(24, true),
    view.getUint32(28, true),
    view.getUint32(32, true),
    view.getUint16(34, true),
  ];

  return new SSRContext({ nChannels, sampleRate, bitDepth });
};

import { SSRContext } from "../ssrctx";
import { AudioDataSource, AudioDataSourceOptions } from "./audio-data-source";
export interface OscillatorProps extends AudioDataSourceOptions {
  frequency: number;
}
export class Oscillator extends AudioDataSource {
  frequency: any;

  constructor(ctx, opts: OscillatorProps) {
    super(ctx, opts);
    this.frequency = opts.frequency;
  }

  read(): Buffer {
    const twopie = 3.1415;
    const frames = Buffer.allocUnsafe(this.ctx.blockSize);
    const phase = (this.ctx.currentTime / this.frequency) * twopie;
    const cyclePerSample = (3.14 * 2 * this.frequency) / this.ctx.sampleRate;
    for (let i = 0; i < 128; i++) {
      const idx = ~~(i / this.ctx.nChannels);
      this.ctx.encode(frames, Math.sin(phase + cyclePerSample * idx) * 0.2, i);
    }
    return frames;
  }
}

import { SSRContext } from "../ssrctx";
import { Readable, Writable, ReadableOptions } from "stream";
export interface AudioDataSourceOptions extends ReadableOptions {
  start?: number;
  end?: number;
}
export class AudioDataSource extends Readable {
  ctx: SSRContext;
  start: number;
  end: number;
  _buffer: Buffer;
  constructor(ctx: SSRContext, opts?: Partial<AudioDataSourceOptions>) {
    super();
    this.ctx = ctx;
    this.start = opts?.start || 0;
    this.end = opts?.end || null;
    //this.on("end", () => this.free());
    ctx.inputs.push(this);
  }
  isActive = () => {
    if (this.start !== null && this.start > this.ctx.currentTime) return false;
    if (this.end !== null && this.end < this.ctx.currentTime) return false;
    if (this._buffer && this._buffer.byteLength === 0) return false;
    return true;
  };
  read(): Buffer {
    if (!this.isActive()) return null;
    const ret = this._buffer.slice(0, this.ctx.blockSize);
    this._buffer = this._buffer.slice(this.ctx.blockSize);
    return ret;
  }
  free(): void {
    this._buffer = null;
  }
}
